svg.script:
const elements = {
  a: [[document.getElementById('a11'), document.getElementById('a12')], [document.getElementById('a21'), document.getElementById('a22')]],
  b: [[document.getElementById('b11'), document.getElementById('b12')], [document.getElementById('b21'), document.getElementById('b22')]],
  c: [[document.getElementById('c11'), document.getElementById('c12')], [document.getElementById('c21'), document.getElementById('c22')]],
  calcText: document.getElementById('calc-text'),
};

const steps = [
  { r: 0, c: 0, text: '(1 * 5) + (2 * 7) = 19', val: 19 },
  { r: 0, c: 1, text: '(1 * 6) + (2 * 8) = 22', val: 22 },
  { r: 1, c: 0, text: '(3 * 5) + (4 * 7) = 43', val: 43 },
  { r: 1, c: 1, text: '(3 * 6) + (4 * 8) = 50', val: 50 },
];
let step = 0;

function updateCalculation() {
  // Clear previous highlights
  document.querySelectorAll('.highlight').forEach(el => el.classList.remove('highlight'));

  const currentStep = steps[step % 4];
  const { r, c, text, val } = currentStep;

  // Highlight row in A
  elements.a[r][0].classList.add('highlight');
  elements.a[r][1].classList.add('highlight');

  // Highlight col in B
  elements.b[0][c].classList.add('highlight');
  elements.b[1][c].classList.add('highlight');

  // Highlight result element
  elements.c[r][c].classList.add('highlight');

  elements.calcText.textContent = text;
  elements.c[r][c].textContent = val;

  step++;
}

setInterval(updateCalculation, 2500);
updateCalculation();
